schema:
{
 "description": "ClusterFluentdConfigSpec defines the desired state of ClusterFluentdConfig",
 "properties": {
  "clusterFilterSelector": {
   "description": "Select cluster filter plugins",
   "properties": {
    "matchExpressions": {
     "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
     "items": {
      "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
      "properties": {
       "key": {
        "description": "key is the label key that the selector applies to.",
        "type": "string"
       },
       "operator": {
        "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
        "type": "string"
       },
       "values": {
        "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "key",
       "operator"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "matchLabels": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
     "type": "object"
    }
   },
   "type": "object"
  },
  "clusterOutputSelector": {
   "description": "Select cluster output plugins",
   "properties": {
    "matchExpressions": {
     "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
     "items": {
      "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
      "properties": {
       "key": {
        "description": "key is the label key that the selector applies to.",
        "type": "string"
       },
       "operator": {
        "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
        "type": "string"
       },
       "values": {
        "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "key",
       "operator"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "matchLabels": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
     "type": "object"
    }
   },
   "type": "object"
  },
  "emit_mode": {
   "description": "Emit mode. If batch, the plugin will emit events per labels matched. Enum: record, batch. will make no effect if EnableFilterKubernetes is set false.",
   "enum": [
    "record",
    "batch"
   ],
   "type": "string"
  },
  "stickyTags": {
   "description": "Sticky tags will match only one record from an event stream. The same tag will be treated the same way. will make no effect if EnableFilterKubernetes is set false.",
   "type": "string"
  },
  "watchedConstainers": {
   "description": "A set of container names. Ignored if left empty.",
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "watchedHosts": {
   "description": "A set of hosts. Ignored if left empty.",
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "watchedLabels": {
   "additionalProperties": {
    "type": "string"
   },
   "description": "Use this field to filter the logs, will make no effect if EnableFilterKubernetes is set false.",
   "type": "object"
  },
  "watchedNamespaces": {
   "description": "A set of namespaces. The whole namespaces would be watched if left empty.",
   "items": {
    "type": "string"
   },
   "type": "array"
  }
 },
 "title": "Cluster Fluentd Config",
 "type": "object"
}
