schema:
{
 "description": "GpuDevicePluginSpec defines the desired state of GpuDevicePlugin.",
 "properties": {
  "enableMonitoring": {
   "description": "EnableMonitoring enables the monitoring resource ('i915_monitoring') which gives access to all GPU devices on given node.",
   "type": "boolean"
  },
  "image": {
   "description": "Image is a container image with GPU device plugin executable.",
   "type": "string"
  },
  "initImage": {
   "description": "InitImage is a container image with tools (e.g., GPU NFD source hook) installed on each node.",
   "type": "string"
  },
  "logLevel": {
   "description": "LogLevel sets the plugin's log level.",
   "minimum": 0,
   "type": "integer"
  },
  "nodeSelector": {
   "additionalProperties": {
    "type": "string"
   },
   "description": "NodeSelector provides a simple way to constrain device plugin pods to nodes with particular labels.",
   "type": "object"
  },
  "preferredAllocationPolicy": {
   "description": "PreferredAllocationPolicy sets the mode of allocating GPU devices on a node. See documentation for detailed description of the policies. Only valid when SharedDevNum \u003e 1 is set.",
   "enum": [
    "balanced",
    "packed",
    "none"
   ],
   "type": "string"
  },
  "resourceManager": {
   "description": "ResourceManager handles the fractional resource management for multi-GPU nodes",
   "type": "boolean"
  },
  "sharedDevNum": {
   "description": "SharedDevNum is a number of containers that can share the same GPU device.",
   "minimum": 1,
   "type": "integer"
  }
 },
 "title": "Gpu Device Plugin",
 "type": "object"
}
