schema:
{
 "description": "LagoonBuildSpec defines the desired state of LagoonBuild",
 "properties": {
  "branch": {
   "description": "Branch contains the branch name used for a branch deployment.",
   "properties": {
    "name": {
     "type": "string"
    }
   },
   "type": "object"
  },
  "build": {
   "description": "Build contains the type of build, and the image to use for the builder.",
   "properties": {
    "bulkId": {
     "type": "string"
    },
    "ci": {
     "type": "string"
    },
    "image": {
     "type": "string"
    },
    "priority": {
     "type": "integer"
    },
    "type": {
     "type": "string"
    }
   },
   "required": [
    "type"
   ],
   "type": "object"
  },
  "gitReference": {
   "type": "string"
  },
  "project": {
   "description": "Project contains the project information from lagoon.",
   "properties": {
    "deployTarget": {
     "type": "string"
    },
    "environment": {
     "type": "string"
    },
    "environmentId": {
     "type": "integer"
    },
    "environmentIdling": {
     "type": "integer"
    },
    "environmentType": {
     "type": "string"
    },
    "gitUrl": {
     "type": "string"
    },
    "id": {
     "type": "integer"
    },
    "key": {
     "format": "byte",
     "type": "string"
    },
    "monitoring": {
     "description": "Monitoring contains the monitoring information for the project in Lagoon.",
     "properties": {
      "contact": {
       "type": "string"
      },
      "statuspageID": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "name": {
     "type": "string"
    },
    "namespacePattern": {
     "type": "string"
    },
    "productionEnvironment": {
     "type": "string"
    },
    "projectIdling": {
     "type": "integer"
    },
    "projectSecret": {
     "type": "string"
    },
    "registry": {
     "type": "string"
    },
    "routerPattern": {
     "type": "string"
    },
    "standbyEnvironment": {
     "type": "string"
    },
    "storageCalculator": {
     "type": "integer"
    },
    "subfolder": {
     "type": "string"
    },
    "uiLink": {
     "type": "string"
    },
    "variables": {
     "description": "Variables contains the project and environment variables from lagoon.",
     "properties": {
      "environment": {
       "format": "byte",
       "type": "string"
      },
      "project": {
       "format": "byte",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "deployTarget",
    "environment",
    "environmentType",
    "gitUrl",
    "key",
    "monitoring",
    "name",
    "productionEnvironment",
    "projectSecret",
    "standbyEnvironment",
    "variables"
   ],
   "type": "object"
  },
  "promote": {
   "description": "Promote contains the information for a promote deployment.",
   "properties": {
    "sourceEnvironment": {
     "type": "string"
    },
    "sourceProject": {
     "type": "string"
    }
   },
   "type": "object"
  },
  "pullrequest": {
   "description": "Pullrequest contains the information for a pullrequest deployment.",
   "properties": {
    "baseBranch": {
     "type": "string"
    },
    "baseSha": {
     "type": "string"
    },
    "headBranch": {
     "type": "string"
    },
    "headSha": {
     "type": "string"
    },
    "number": {
     "type": "string"
    },
    "title": {
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "required": [
  "build",
  "gitReference",
  "project"
 ],
 "title": "Lagoon Build",
 "type": "object"
}
