schema:
{
 "description": "The specification of the Schema Registry instance.",
 "properties": {
  "compatibilityLevel": {
   "default": "forward",
   "description": "The default schema compatibility level in a subject. Can be none, backward, backward_transitive, forward, forward_transitive, full, and full_transitive.",
   "type": "string"
  },
  "cpuLimit": {
   "default": "",
   "description": "The cap on CPU usage for the Schema Registry container. Default is to leave unset. Example 1000m limits to 1 CPU.",
   "type": "string"
  },
  "cpuRequest": {
   "default": "",
   "description": "The requested CPU allocation for the Schema Registry container. Default is to leave unset. Example 1000m requests 1 CPU.",
   "type": "string"
  },
  "listener": {
   "default": "tls",
   "description": "The name of the Kafka listener to use to connect.",
   "type": "string"
  },
  "memoryLimit": {
   "default": "",
   "description": "The cap on memory usage for the Schema Registry container. Default is to leave unset. Example 1000M limits to 1000 megabytes.",
   "type": "string"
  },
  "memoryRequest": {
   "default": "",
   "description": "The requested memory for the Schema Registry container. Default is to leave unset. Example: `768M` requests 768 megabytes.",
   "type": "string"
  },
  "registryImage": {
   "default": "confluentinc/cp-schema-registry",
   "description": "The Docker image for the Confluent Schema Registry.",
   "type": "string"
  },
  "registryImageTag": {
   "default": "7.2.1",
   "description": "The Docker image tag for the Confluent Schema Registry. Use this property to control the version of the deployed schema registry.",
   "type": "string"
  },
  "securityProtocol": {
   "default": "SSL",
   "description": "The security protocol for the Schema Registry to communicate with Kafka. Default is SSL. Can be SSL, PLAINTEXT, SASL_PLAINTEXT, or SASL_SSL.",
   "type": "string"
  },
  "serviceType": {
   "default": "ClusterIP",
   "description": "The type of service to create for the registry. Default is ClusterIP. Can be NodePort to publish the registry externally.",
   "type": "string"
  },
  "strimzi-version": {
   "default": "v1beta2",
   "description": "Deprecated. Use strimziVersion instead.",
   "type": "string"
  },
  "strimziVersion": {
   "default": "v1beta2",
   "description": "The version of the Strimzi Custom Resource API to use. The correct value depends on the deployed version of Strimzi.",
   "type": "string"
  }
 },
 "title": "Strimzi Schema Registry",
 "type": "object"
}
