schema:
{
 "description": "APDosLogConfSpec defines the desired state of APDosLogConf",
 "properties": {
  "content": {
   "properties": {
    "format": {
     "enum": [
      "splunk",
      "arcsight",
      "user-defined"
     ],
     "type": "string"
    },
    "format_string": {
     "type": "string"
    },
    "max_message_size": {
     "pattern": "^([1-9]|[1-5][0-9]|6[0-4])k$",
     "type": "string"
    }
   },
   "type": "object"
  },
  "filter": {
   "properties": {
    "attack-signatures": {
     "default": "top 10",
     "pattern": "^(none|all|top ([1-9]|[1-9][0-9]|[1-9][0-9]{2,4}|100000))$",
     "type": "string"
    },
    "bad-actors": {
     "default": "top 10",
     "pattern": "^(none|all|top ([1-9]|[1-9][0-9]|[1-9][0-9]{2,4}|100000))$",
     "type": "string"
    },
    "traffic-mitigation-stats": {
     "default": "all",
     "enum": [
      "none",
      "all"
     ],
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "AP Dos Log Conf",
 "type": "object"
}
