schema:
{
 "description": "APLogConfSpec defines the desired state of APLogConf",
 "properties": {
  "content": {
   "properties": {
    "escaping_characters": {
     "items": {
      "properties": {
       "from": {
        "type": "string"
       },
       "to": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "format": {
     "enum": [
      "splunk",
      "arcsight",
      "default",
      "user-defined",
      "grpc"
     ],
     "type": "string"
    },
    "format_string": {
     "type": "string"
    },
    "list_delimiter": {
     "type": "string"
    },
    "list_prefix": {
     "type": "string"
    },
    "list_suffix": {
     "type": "string"
    },
    "max_message_size": {
     "pattern": "^([1-9]|[1-5][0-9]|6[0-4])k$",
     "type": "string"
    },
    "max_request_size": {
     "pattern": "^([1-9]|[1-9][0-9]|[1-9][0-9]{2}|1[0-9]{3}|20[1-3][0-9]|204[1-8]|any)$",
     "type": "string"
    }
   },
   "type": "object"
  },
  "filter": {
   "properties": {
    "request_type": {
     "enum": [
      "all",
      "illegal",
      "blocked"
     ],
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "AP Log Conf",
 "type": "object"
}
