schema:
{
 "description": "DatabaseSpec defines the desired state of Database",
 "properties": {
  "databaseInstance": {
   "description": "Database instance specific details",
   "properties": {
    "credentialSecret": {
     "description": "Name of the secret holding the credentials for the database instance (password and ssh key)",
     "type": "string"
    },
    "databaseInstanceName": {
     "default": "database_instance_name",
     "description": "Name of the database instance",
     "type": "string"
    },
    "databaseNames": {
     "default": [
      "alpha",
      "beta"
     ],
     "description": "Name of the database to be provisiond in the database instance",
     "items": {
      "type": "string"
     },
     "minItems": 1,
     "type": "array"
    },
    "profiles": {
     "properties": {
      "compute": {
       "properties": {
        "id": {
         "type": "string"
        },
        "versionId": {
         "type": "string"
        }
       },
       "type": "object"
      },
      "dbParam": {
       "properties": {
        "id": {
         "type": "string"
        },
        "versionId": {
         "type": "string"
        }
       },
       "type": "object"
      },
      "network": {
       "properties": {
        "id": {
         "type": "string"
        },
        "versionId": {
         "type": "string"
        }
       },
       "type": "object"
      },
      "software": {
       "properties": {
        "id": {
         "type": "string"
        },
        "versionId": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "size": {
     "default": 10,
     "description": "Size of the database instance",
     "minimum": 10,
     "type": "integer"
    },
    "timezone": {
     "default": "UTC",
     "type": "string"
    },
    "type": {
     "default": "postgres",
     "enum": [
      "mysql",
      "postgres",
      "mongodb"
     ],
     "type": "string"
    }
   },
   "required": [
    "credentialSecret",
    "databaseInstanceName",
    "databaseNames",
    "type"
   ],
   "type": "object"
  },
  "ndb": {
   "description": "Details of the NDB installation",
   "properties": {
    "clusterId": {
     "type": "string"
    },
    "credentialSecret": {
     "description": "Name of the secret holding the credentials for NDB (username and password)",
     "type": "string"
    },
    "server": {
     "type": "string"
    },
    "skipCertificateVerification": {
     "default": false,
     "description": "Skip server's certificate and hostname verification",
     "type": "boolean"
    }
   },
   "required": [
    "clusterId",
    "credentialSecret",
    "server"
   ],
   "type": "object"
  }
 },
 "required": [
  "databaseInstance",
  "ndb"
 ],
 "title": "Database",
 "type": "object"
}
