schema:
{
 "properties": {
  "nonProductionOptions": {
   "properties": {
    "disableClusterPodAntiAffinity": {
     "description": "It is a best practice, on non-containerized environments, when running production workloads, to run each database server on a different server (virtual or physical), i.e., not to co-locate more than one database server per host.\n\nThe same best practice applies to databases on containers. By default, StackGres will not allow to run more than one StackGres or Distributed Logs pod on a given K8s node. Set this property to true to allow more than one StackGres pod per node.\n",
     "type": "boolean"
    }
   },
   "type": "object"
  },
  "persistentVolume": {
   "description": "Pod's persistent volume configuration",
   "properties": {
    "size": {
     "description": "Size of the PersistentVolume to be created for the pod of the cluster for distributed logs. Size must be specified either in Mebibytes, Gibibytes or Tebibytes (multiples of 2^20, 2^30 or 2^40, respectively).\n",
     "pattern": "^[0-9]+(\\.[0-9]+)?(Mi|Gi|Ti)$",
     "type": "string"
    },
    "storageClass": {
     "description": "Name of an existing StorageClass in the K8s cluster, that will be used to create the PersistentVolumes for the instances of this cluster.\n",
     "type": "string"
    }
   },
   "required": [
    "size"
   ],
   "type": "object"
  }
 },
 "required": [
  "persistentVolume"
 ],
 "title": "SG Distributed Logs",
 "type": "object"
}
