      "accessorID": {
       "type": "string"
      },
      "clientName": {
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "enabled"
   ],
   "type": "object"
  },
  "openshift": {
   "description": "Openshift is the config containing labels and annotations",
