      "deny": {
       "description": "Policy contains policies relating to privilegedNamespaces",
       "properties": {
        "privilegedNamespaces": {
         "description": "PrivilegedNamespaces contains groups/users",
         "properties": {
          "groups": {
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "users": {
           "items": {
            "type": "string"
