schema:
{
 "description": "ResourceSupervisorSpec defines the desired state of ResourceSupervisor",
 "properties": {
  "argocd": {
   "description": "ArgoCD contains details about ArgoCD to add Hibernation schedule to it",
   "properties": {
    "appProjects": {
     "description": "AppProjects lists the ArgoCD AppProjects that will follow the Hibernation schedule for this resource",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "namespace": {
     "description": "Namespace is the ArgoCD namespace where the AppProjects are residing in",
     "type": "string"
    }
   },
   "required": [
    "appProjects",
    "namespace"
   ],
   "type": "object"
  },
  "hibernation": {
   "description": "Hibernation contains the schedule during which the tenant will be sleeping",
   "properties": {
    "sleepSchedule": {
     "description": "SleepSchedule contains the schedule during which the resources within the tenant's namespaces will be put to sleep",
     "type": "string"
    },
    "wakeSchedule": {
     "description": "WakeSchedule contains the schedule during which the resources within the tenant's namespaces will be awoken",
     "type": "string"
    }
   },
   "required": [
    "sleepSchedule",
    "wakeSchedule"
   ],
   "type": "object"
  }
 },
 "required": [
  "hibernation"
 ],
 "title": "Resource Supervisor",
 "type": "object"
}
