schema:
{
 "description": "PolicySpec is the spec of the Policy resource. The spec includes multiple fields, where each field represents a different policy. Only one policy (field) is allowed.",
 "properties": {
  "accessControl": {
   "description": "AccessControl defines an access policy based on the source IP of a request. policy status: production-ready",
   "properties": {
    "allow": {
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "deny": {
     "items": {
      "type": "string"
     },
     "type": "array"
    }
   },
   "type": "object"
  },
  "egressMTLS": {
   "description": "EgressMTLS defines an Egress MTLS policy. policy status: preview",
   "properties": {
    "ciphers": {
     "type": "string"
    },
    "protocols": {
     "type": "string"
    },
    "serverName": {
     "type": "boolean"
    },
    "sessionReuse": {
     "type": "boolean"
    },
    "sslName": {
     "type": "string"
    },
    "tlsSecret": {
     "type": "string"
    },
    "trustedCertSecret": {
     "type": "string"
    },
    "verifyDepth": {
     "type": "integer"
    },
    "verifyServer": {
     "type": "boolean"
    }
   },
   "type": "object"
  },
  "ingressMTLS": {
   "description": "IngressMTLS defines an Ingress MTLS policy. policy status: preview",
   "properties": {
    "clientCertSecret": {
     "type": "string"
    },
    "verifyClient": {
     "type": "string"
    },
    "verifyDepth": {
     "type": "integer"
    }
   },
   "type": "object"
  },
  "jwt": {
   "description": "JWTAuth holds JWT authentication configuration. policy status: preview",
   "properties": {
    "realm": {
     "type": "string"
    },
    "secret": {
     "type": "string"
    },
    "token": {
     "type": "string"
    }
   },
   "type": "object"
  },
  "oidc": {
   "description": "OIDC defines an Open ID Connect policy.",
   "properties": {
    "authEndpoint": {
     "type": "string"
    },
    "clientID": {
     "type": "string"
    },
    "clientSecret": {
     "type": "string"
    },
    "jwksURI": {
     "type": "string"
    },
    "redirectURI": {
     "type": "string"
    },
    "scope": {
     "type": "string"
    },
    "tokenEndpoint": {
     "type": "string"
    }
   },
   "type": "object"
  },
  "rateLimit": {
   "description": "RateLimit defines a rate limit policy. policy status: preview",
   "properties": {
    "burst": {
     "type": "integer"
    },
    "delay": {
     "type": "integer"
    },
    "dryRun": {
     "type": "boolean"
    },
    "key": {
     "type": "string"
    },
    "logLevel": {
     "type": "string"
    },
    "noDelay": {
     "type": "boolean"
    },
    "rate": {
     "type": "string"
    },
    "rejectCode": {
     "type": "integer"
    },
    "zoneSize": {
     "type": "string"
    }
   },
   "type": "object"
  },
  "waf": {
   "description": "WAF defines an WAF policy. policy status: preview",
   "properties": {
    "apPolicy": {
     "type": "string"
    },
    "enable": {
     "type": "boolean"
    },
    "securityLog": {
     "description": "SecurityLog defines the security log of a WAF policy.",
     "properties": {
      "apLogConf": {
       "type": "string"
      },
      "enable": {
       "type": "boolean"
      },
      "logDest": {
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Policy",
 "type": "object"
}
