schema:
{
 "description": "TCPMappingSpec defines the desired state of TCPMapping",
 "properties": {
  "address": {
   "type": "string"
  },
  "ambassador_id": {
   "description": "AmbassadorID declares which Ambassador instances should pay attention to this resource.  May either be a string or a list of strings.  If no value is provided, the default is: \n    ambassador_id:    - \"default\"",
   "items": {
    "type": "string"
   },
   "oneOf": [
    {
     "type": "string"
    },
    {
     "type": "array"
    }
   ]
  },
  "circuit_breakers": {
   "items": {
    "properties": {
     "max_connections": {
      "type": "integer"
     },
     "max_pending_requests": {
      "type": "integer"
     },
     "max_requests": {
      "type": "integer"
     },
     "max_retries": {
      "type": "integer"
     },
     "priority": {
      "enum": [
       "default",
       "high"
      ],
      "type": "string"
     }
    },
    "type": "object"
   },
   "type": "array"
  },
  "cluster_tag": {
   "type": "string"
  },
  "enable_ipv4": {
   "type": "boolean"
  },
  "enable_ipv6": {
   "type": "boolean"
  },
  "host": {
   "type": "string"
  },
  "idle_timeout_ms": {
   "description": "FIXME(lukeshu): Surely this should be an 'int'?",
   "type": "string"
  },
  "port": {
   "description": "Port isn't a pointer because it's required.",
   "type": "integer"
  },
  "resolver": {
   "type": "string"
  },
  "service": {
   "type": "string"
  },
  "tls": {
   "description": "BoolOrString is a type that can hold a Boolean or a string.",
   "oneOf": [
    {
     "type": "string"
    },
    {
     "type": "boolean"
    }
   ]
  },
  "weight": {
   "type": "integer"
  }
 },
 "required": [
  "port",
  "service"
 ],
 "title": "TCP Mapping",
 "type": "object"
}
