schema:
{
 "description": "TracingServiceSpec defines the desired state of TracingService",
 "properties": {
  "ambassador_id": {
   "description": "AmbassadorID declares which Ambassador instances should pay attention to this resource.  May either be a string or a list of strings.  If no value is provided, the default is: \n    ambassador_id:    - \"default\"",
   "items": {
    "type": "string"
   },
   "oneOf": [
    {
     "type": "string"
    },
    {
     "type": "array"
    }
   ]
  },
  "config": {
   "properties": {
    "access_token_file": {
     "type": "string"
    },
    "collector_cluster": {
     "type": "string"
    },
    "collector_endpoint": {
     "type": "string"
    },
    "collector_endpoint_version": {
     "enum": [
      "HTTP_JSON_V1",
      "HTTP_JSON",
      "HTTP_PROTO"
     ],
     "type": "string"
    },
    "collector_hostname": {
     "type": "string"
    },
    "service_name": {
     "type": "string"
    },
    "shared_span_context": {
     "type": "boolean"
    },
    "trace_id_128bit": {
     "type": "boolean"
    }
   },
   "type": "object"
  },
  "driver": {
   "enum": [
    "lightstep",
    "zipkin",
    "datadog"
   ],
   "type": "string"
  },
  "sampling": {
   "properties": {
    "client": {
     "type": "integer"
    },
    "overall": {
     "type": "integer"
    },
    "random": {
     "type": "integer"
    }
   },
   "type": "object"
  },
  "service": {
   "type": "string"
  },
  "tag_headers": {
   "items": {
    "type": "string"
   },
   "type": "array"
  }
 },
 "required": [
  "driver",
  "service"
 ],
 "title": "Tracing Service",
 "type": "object"
}
